import numpy as np

def win_probability(size):

    A_count = 0 
    B_count = 0 

    for i in range(size): 
        A_role = np.random.randint(1,7)

        if A_role == 6:       
            A_count+=1     
        else:              
            B_role = np.random.randint(1,7)
            if B_role == 6:   
                B_count+=1
    return A_count/(A_count+B_count)
